mapper.select:
select * from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and number like '%${number}%'


        status = #{status}


        and phone like '%${phone}%'


    and user_id = #{userId}


    order by order_time desc

select count(id) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}


        and user_id = #{userId}

select sum(amount) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}


        and user_id = #{userId}
